let h;
function setup() {
	createCanvas(windowWidth, windowHeight)
	h=windowHeight
	colorMode(HSB,360,100,100)
}

let xpulse = 0;
let ypulse = 0;
let br = 100;
let hu = 320;

function draw() {
	background(0,0,0)
	noStroke()
	fill(hu,100,br) 
	if(br>0){br-=0.5} else {br=100;if(hu>220){hu-=9} else {hu=320;}} 

	ellipse(windowWidth/2,h/4,600,600)
	pulse()
	star()
}

function pulse(){
	if (xpulse > windowWidth){xpulse=0;}
	if (ypulse > windowHeight){ypulse=0;}
	if (frameCount%3==0){
	stroke(0,0,100)
	strokeWeight(4)
	line(xpulse,0,xpulse,windowHeight);
	line(0,ypulse,windowWidth,ypulse);
	xpulse+=7
		ypulse+=7
	}
}

function star(){
	noStroke();
	if (random(42)<1){
		for (i=0;i<17;i++){
			let x = random(windowWidth);
			let y = random(windowHeight);
			let baldessari = random(100,300)
			fill(50,0,100)
			ellipse(x,y, baldessari,baldessari)
		}
	}
}
