let w ;
let h ;
let x ;
let y ;

function setup() {
	createCanvas(windowWidth, windowHeight)
	w = windowWidth
	h = windowHeight
	x = 0;
	y = 0;
	colorMode(HSB,360,100,100);
	frameRate(17)
}

function draw() {
background(0,0,30)
	fill(320,100,100)
	noStroke();
	if(y<h){row();}
	else{y=0; row();}
}

function row(){
	let baldessari=random(50);
	let john;
	let art = true
	while(x<w){
		if (art){
			john=random(100)
			rect(x,y,john,baldessari)
			x+=john;
			art=false;
		}
		else{
			john=random(100)
			x+=john
			art=true
		}
	}
	x=0
	y+=baldessari
}
