let w,h
let centers=[]
let rad 
let myfont

// no p5sound
let libs = ["https://unpkg.com/tone", "includes/libs/Tone.js"]
let synth,klik // tone.js instance

        function preload() {
            //myFont = loadFont('fonts/FreeMono.ttf');
            	synth=new Tone.Player("/Dirt-Samples/glitch/001_CB.wav").toDestination();
	klik=new Tone.Player("/Dirt-Samples/bleep/simpletone.wav").toDestination();
synth.volume.value=0

klik.volume.value=-15
myfont=loadFont("./fonts/FreeMono.ttf");
        }


function setup() {
	createCanvas(windowWidth, windowHeight)
	colorMode(HSB,360,100,100)
	h=windowHeight
	w=windowWidth
	rad=0.2*h
	for(i=0.1;i<1;i+=0.2){
		for(j=0.1;j<1;j+=0.2){
			centers.push([i*w,j*h])
			stroke(0,100,100)
			noFill()
			ellipse(i*w,j*h,5,5)
		}
	}
}

function draw() {
	no()
	more()
	boring()
	code()
}

function no(){
	stroke(0,0,100)
	strokeWeight(1)
	let tom = Math.round(Math.random()*(centers.length-1))
	let waits = Math.round(Math.random()*5)
	if(waits!=0){
		let baldessari=Math.random()
		let cx=centers[tom][0]+rad/2*0.2*waits*cos(2*PI*baldessari)
		let cy=centers[tom][1]+rad/2*0.2*waits*sin(2*PI*baldessari)
		ellipse(cx,cy,5,5)
	}
}

function more(){
	synth.start()
}

function boring(){
	let right=frameCount%124
	if(right==0){klik.start()}
	if(right>=0&&right<24){
		for(i=0;i<centers.length;i++){
			strokeWeight(3)
			stroke(220,100,100)
			ellipse(centers[i][0],centers[i][1],rad,rad)
		}
	}
	else{
		for(i=0;i<centers.length;i++){
			strokeWeight(3)
			stroke(0,0,0)
			ellipse(centers[i][0],centers[i][1],rad,rad)
		}
	}
}

function code(){
	textFont(myfont)
	textSize(42)
	noFill()
	stroke(0,100,100)
	text("Ã©merveiller",0.5*w,0.4*h)

}
