// no p5sound
let libs = ["https://unpkg.com/tone", "includes/libs/Tone.js"]
let drone,radar
let myfont
let w,h
let centers=[]
let ratio=0.2
let rad

function preload() {
  	drone=new Tone.Player("/sounds/drone.wav").toDestination();
	radar=new Tone.Player("/sounds/radar.mp3").toDestination();
	drone.volume.value=-25
	radar.volume.value=-15
	myfont=loadFont("./fonts/FreeMono.ttf");
}


function setup() {
	createCanvas(windowWidth, windowHeight)
	colorMode(HSB,360,100,100)
	w=windowWidth
	h=windowHeight
	rad=ratio*h
	for(i=0.1;i<1;i+=ratio){
		for(j=0.1;j<1;j+=ratio){
			centers.push([i*w,j*h])
			noFill()
			stroke(0,100,100)
			ellipse(i*w,j*h,3,3)
		}
	}
}

function draw() {
	no()
	more()
	boring()
	code()
}

function code(){
	textFont(myfont)
	textSize(42)
	noFill()
	stroke(0,100,100)
	text("Ã©merveiller par l'art",w/2,h/2)
}

function boring(){
	let john=Math.floor(Math.random()*centers.length)
	let baldessari=Math.floor(Math.random()*centers.length)
	if(Math.random()*2424<42){
		line(centers[john][0],centers[john][1],centers[baldessari][0],centers[baldessari][1])}

}

function no(){
	drone.start()
	let john=Math.floor(Math.random()*centers.length)
	let baldessari=(Math.floor(Math.random()*5)+1)*0.2*(rad/2)
	let beauty = Math.random()*2*PI
	let cx=centers[john][0]+baldessari*cos(beauty)
	let cy=centers[john][1]+baldessari*sin(beauty)
	stroke(0,0,100)
	ellipse(cx,cy,3,3)
}

function more(){
	let r=frameCount%242
	if(r==0){radar.start()}
	if (r>=0&r<42){stroke(230,100,100)}
	if (r>=42&&r<84){stroke(0,100,100)}
	if(r>=84){stroke(0,0,0)}
	for (i=0;i<centers.length;i++){
		ellipse(centers[i][0],centers[i][1],rad,rad)
	}
}
